import React from 'react';
import {marauder, immortal, units} from "./SC2Data"
import './App.css';
import {Attacker, canAttackTargetDefender, Damage} from "./components/Attacker"
import {Defender} from "./components/Defender"
import {Upgrade} from "./components/Upgrade"
import { UnitSelector } from './components/UnitSelector';
import { textChangeRangeIsUnchanged } from 'typescript';


function App() {
  const [shieldsResearch, setShieldsResearch] = React.useState(0)
  const [armorResearch, setArmorResearch] = React.useState(0)
  const [attackResearch, setAttackResearch] = React.useState(0);

  const [attackIndex, setAttackIndex] = React.useState(null)

  const [attacker, setAttacker] = React.useState(null)
  const [defender, setDefender] = React.useState(null)

  const setDefenderShields = (value) => {
    setDefender({
      ...defender,
      base:{
        ...defender.base,
        shields: value
      }
    })
  }

  React.useEffect(() => {
    if(attacker?.attacks.length > 1 || attacker?.attacks.length === 0){
      setAttackIndex(null)
      } else {
        setAttackIndex(0)
      }
  }, [attacker])

  const RandomButton = ({ position }) => {
    const randomUnit = units[Math.floor(Math.random() * units.length)]

    return <div className={"random-button"}>
    <button onClick={() => position(randomUnit)}>random : {randomUnit.name}</button>
      </div>
  }

  const attack_info = attacker ? 
    <>
    <Attacker 
      unit={attacker} 
      defender={defender} 
      attackChoice={[attackIndex, setAttackIndex]}/>
      </>
    : <RandomButton position={setAttacker} /> ;

  const defender_info = defender ?
  <>
  <Defender 
    unit={defender} 

    changeStats={[setDefenderShields]}
  />
  </> : <RandomButton position={setDefender} /> ;

    return (
      <div className="App">
        <div className={"arena"}>
          <div className={"container"}>
            {attack_info}
            <UnitSelector setUnit={setAttacker} currentUnit={attacker}/>
          </div>
          <div className={"container"}>
            <Middle 
              attacker={attacker}
              defender={defender}
              aidx={attackIndex}
              research={[attackResearch, setAttackResearch, shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch]}
              setDefender={setDefender}
            />
          </div>
          <div className={"container"}>
            {defender_info}
            <UnitSelector setUnit={setDefender} currentUnit={defender}/>
          </div>
        </div>
      </div>
    );
}


const DPS = ({attacker, defender, aidx,  attackResearch, shieldsResearch, armorResearch, setDefender}) => {
  console.log("damage", attacker, defender, aidx, [ attackResearch, shieldsResearch, armorResearch])
  const d = new Damage(attacker, defender, aidx, [ attackResearch, shieldsResearch, armorResearch])
  const backup = new Damage(JSON.parse(JSON.stringify(attacker)),
    JSON.parse(JSON.stringify(defender)), aidx, 
    [ attackResearch, shieldsResearch, armorResearch]
    )
  const active = canAttackTargetDefender(attacker?.attacks[aidx], defender)
  let text
  if(active){
    if(d.oneShot()[0]){
      text = <>Attack: {d.oneShot()[0].totalDamage}</>
    } else {
      text = <>Victory (reset)</>
    }
  } else {
    if (attacker && defender) {
      if(attacker.attacks.map(attack => {
              return canAttackTargetDefender(attack, defender)
            }).filter(item => item).length > 0){
              text = <>Select a valid attack</>
            }
      else{
        text = <>{attacker.name} can't attack {defender.name}</>
      }

    } else {
      text =  <>Awaiting...</>
    }
  }

  const cns = [];
  cns.push("button")
  cns.push(active ? "active": "inactive")

  const resetDefender = () => {
    if(defender?.name){
     setDefender(units.find(unit => unit.name === defender.name))
     }
  }

  const updateHealth = () => {
    const newValues = d.oneShot()[d.oneShot().length - 1];
    console.log("updating health", d.oneShot())
    if(newValues){
      setDefender(
        {
          ...defender,
          base: {
            ...defender.base,
            health: newValues.health,
            shields: newValues.shields,
          }
        }
      )
    } else {
      resetDefender()
    }
  } 
  return <>
    <button className={cns.join(" ")} onClick={() => updateHealth()}>{text}</button>
    {d.oneShot()[0]? <> {backup.eliminate()} attacks to elimanate <button onClick={() => resetDefender()}>Reset</button> </> : null}

    </>
}

const Middle = ({attacker, defender, aidx, research, setDefender}) => {
  const BrawlButton = ({attacker, defender, aidx, attackIndex, attackResearch, shieldsResearch, armorResearch}) => {
    const targetable = canAttackTargetDefender(attacker.attacks[attackIndex], defender)
    return <>{
      targetable
    }</>
  }

  if(!research){
    return null;
  } else {
    console.log('research', research)
    const [attackResearch, setAttackResearch, shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch] = research
    console.log('setAttackResearch', setAttackResearch)
    const upgrades =  
    <>
    <DPS attacker={attacker} defender={defender} aidx={aidx} 
        attackResearch={attackResearch} armorResearch={armorResearch} shieldsResearch={shieldsResearch} 
        setDefender={setDefender}/> 

    <div className={"unit-stats"}>
    {defender?.base.shields? <>
      🧊{defender.base.shields}</>:
      null }
    <>♥️ {defender?.base.health}</>
    </div>
    <div className={"upgrades"}>

      <div>
        <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />

      </div>

      {defender ? 
      <div>
        {defender?.research.shields?
        <div>
          <Upgrade researchKind={"shields"} updateResearch={setShieldsResearch} value={shieldsResearch} />
            {defender.base.shields?
            <h1>🧊 {shieldsResearch}</h1>:
            null
            }
        </div>:
        null
      } <div>
          <Upgrade researchKind={"armor"} updateResearch={setArmorResearch} value={armorResearch} />
          <div><h1>🛡 {defender.base.armor} + {armorResearch*defender.research.armor}</h1></div>
        </div>
      </div>:
      null
       }
      <div>
        Modifiers
      </div>
      </div>
    </>

    if(attacker && defender){
      return <>{upgrades}</>
    } else {
      return upgrades 
    }
  }

  // console.log('attacker', attacker)
  // console.log('defender', defender)
  // console.log('research', research)

  // return(

  //   );
}


export default App;
