import React from "react"

export const useUnits = () => {
  const [response, setResponse] = React.useState(null)

  const getUnits = async () =>{
    fetch('http://localhost:5000/api/units')
      .then(res => res.json())
      .then(res =>  {
        setResponse(res.units)
      })
      .catch(err => {
        console.error("error fetching", err)
      })
  }
  React.useEffect(() => {
    getUnits()
  }, [])

  return response
}

export const Unit = ({unit, weaknesses=[]}) => {

  return <div className={"unit-card"}>
      <div className={"unit-name"}>{unit.name}</div>
      <div className={"unit-profile"}>
        <img src={unit.img} alt={`portrait for ${unit.name}`}></img>

      </div>
      {unit.types.map((( type, index ) => {
           return <div key = {index}> 
           {type}
           </div>
         }))}

  </div>
}

export const UnitImg = ({unit}) => {
  return <img style={{widtH: "100px"}} src={unit.img} alt={`portrait for ${unit.name}`}></img>
}
