import React from "react"
import {units} from "../SC2Data"
import { useSpring, animated } from 'react-spring'

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      barracks: "/barracks.png",
      factory: "/factory.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

const camel2str = (str) => {
   // adding space between strings
  const result = str.replace(/([A-Z])/g,' $1');

  // converting first character to uppercase and join it to the final string
  const final = result.charAt(0).toUpperCase()+result.slice(1);
  return final
}

export const UnitSelector = ({setUnit}) => {
  const Production = ({structure, path}) => {
    const [hidden, setHidden] = React.useState(false)
    const subUnits = units.filter(unit => unit.structure === structure)
    let display;

    if(hidden){
      display = <></>
    } else {
      display = <div className={"units-selector"}>
      {subUnits.map((unit,index) => {
        let inlineStyle = {
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundImage: `url(${ unit.img})`};
        return <div key={index} onClick={() => setUnit(unit)} className={"unit-selection"} style={inlineStyle}>
          <header><h1>{unit.name}</h1></header>
        </div>
      })}
      </div>
    }


    let inlineStyle = {
      backgroundSize: 'contain',
      backgroundRepeat: 'no-repeat',
      backgroundImage: `url(${ path})`};

    return <><div className={"unit-selection"} style={inlineStyle} onClick={() => setHidden(!hidden)}>
    <header><h1>{camel2str(structure)}</h1></header>
    </div>

    {display}
    </>
  }

  const Faction = ({data}) => {
    const [hidden, setHidden] = React.useState(false);
    const menuAppear = useSpring({
      transform: !hidden ? "translate3D(0,0,0)" : "translate3D(-400px,-40px,0)",
      opacity: hidden ? 0 : 1,
     })

    const imgBackground = useSpring({
      background: hidden ?  "#ebebeb" : "#6ce2ff",
    })

    return <>
      <animated.div style={imgBackground}><img src={data.icon} onClick={() => setHidden(!hidden)}></img></animated.div>

        {hidden ? null : 
        <animated.div style={menuAppear}>
          {
            Object.entries(data.production).map(([structure, path]) => {
              return <Production key={structure} structure={structure} path={path} />
            })
          }
        </animated.div>
      }
      </>
  }

  return Object.entries(factions).map( ([key, value]) => {
    return (
      <div key={key} className={"faction"}>
        <Faction data={value} />
      </div>
    );
  })


}
