import React from "react"
import {Unit} from "./Unit"
// import { useSpring, animated } from 'react-spring'

import "./UnitSelector.css"

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      barracks: "/barracks.png",
      factory: "/factory.png",
      starport: "/starport.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

const camel2str = (str) => {
   // adding space between strings
  const result = str.replace(/([A-Z])/g,' $1');

  // converting first character to uppercase and join it to the final string
  const final = result.charAt(0).toUpperCase()+result.slice(1);
  return final
}



export const RandomButton = ({display, randomize, randomText }) => {
  let button;

  if(display){
    button = <span onClick={() => randomize()}>
                {randomText || <>ðŸŽ²</>}
            </span>
  } else {
    button = <button>Loading Units...</button>
  }

  return <div className={"random-button"}>
      {button}
    </div>
}

const ResetButton = ({unit, setUnit}) => {
return <div className={"reset-button"}>
        <span onClick={() => setUnit(null)}>ðŸ’£ clear</span>
      </div>
}

const ButtonGroup = ({unit, setUnit, randomizeUnit}) => {
  return <div className={"button-group"}> 
    <RandomButton randomize={randomizeUnit} /> 
    {unit ? <ResetButton setUnit={setUnit} /> : null}
  </div>
}

export const UnitSelector = (props) => {
  const [setUnit, currentUnit, units] = [props.setUnit, props.currentUnit, props.units]
  const [searchText, setSearchText] = React.useState("")
  const inputText = <input type={"unit-search"} placeholder={"...unit name"} onChange={(e) => setSearchText(e.target.value)} value={searchText}/>

  const showUnits = (subUnits) => {
    const classes = ["unit-selection"]

    // activeUnit = ? "#367180": "#";
    return subUnits.map((unit,index) => {
      let tmpClasses = classes
      let inlineStyle = {
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'right',
        backgroundImage: `url(${ unit.img})`,
      };
      if( currentUnit?.name === unit.name){
        tmpClasses = [...classes, "active-unit"]
      } else {
        tmpClasses = classes
      }
      return <div key={index} onClick={() => setUnit(unit)} className={tmpClasses.join(" ")} style={inlineStyle}>
        <header><h1>{unit.name}</h1></header>
      </div>
    })
  }

  const Production = ({structure, path, units}) => {
    const [hidden, setHidden] = React.useState(true)
    const subUnits = units ? units.filter(unit => unit.structure === structure) : null;

    let display = hidden? <></> :
      <div className={"units-selector"}>{showUnits(subUnits)}</div>;

    let inlineStyle = {
      backgroundSize: 'contain',
      backgroundRepeat: 'no-repeat',
      backgroundImage: `url(${ path})`,
    };

    if(!units) return null;

    return <div>
        <div className={"production-facility"} style={inlineStyle} onClick={() => setHidden(!hidden)}>
          <header><h1>{camel2str(structure)}</h1></header>
        </div>

        <div>{display}</div>
    </div>
  }

  const Faction = ({data, units}) => {
    const [hidden, setHidden] = React.useState(true);

    const imgBackground = ({
      width:"100%",
    })

    return <div style={imgBackground} >

      <div onClick={() => setHidden(!hidden)}>
        <div>{}</div>
        <img src={data.icon} ></img>
        </div>
        {hidden ? null : 
        <div>
          {
            Object.entries(data.production).map(([structure, path]) => {
              return <Production 
                        key={structure} 
                        structure={structure} 
                        path={path}
                        units={units} />
            })
          }
        </div>
      }
      </div>
  }

  const randomElement = (arr) => {
    return arr[Math.floor(Math.random()*arr.length)]
  }

  const randomizeUnit = () => {
    setUnit(randomElement(units))
  }

  const inputArea = <div>
    <div className={"input-area"}>{inputText} <ButtonGroup unit={currentUnit} setUnit={setUnit} randomizeUnit={randomizeUnit}/></div>
    <div>
      {units?.length}
      <Unit unit={currentUnit} setUnit={setUnit}></Unit>
      {props.children}
    </div>
  </div>

  if(units){
    if(searchText.length > 0){
      let subUnits = units.filter(unit => unit.name.toLowerCase().includes(searchText.toLowerCase()))
      return <>
      <>{inputArea}</>
        {showUnits(subUnits)}
      </>
    } else {
      return <>
        {inputArea}
        {Object.entries(factions).map( ([key, value]) => {
        return (
          <div key={key} className={"faction"}>
            <Faction data={value} label={key} units={units} />
          </div>
        );
      })}
      </>
    }
  } else {
    console.log('units from selector', units)
    return "Loading"
  }



}
