import React from 'react';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import {Attacker} from "./components/Attacker"
import {Defender} from "./components/Defender"
function App() {
  const [damageDealt, setDamage] = React.useState(null)
  const [defenseApplied, setDefenseApplied] = React.useState(null)
  const [guardian, setGuardian] = React.useState(0)


  const attacker = marauder;
  const defender = immortal

  const damageReceived = () => {
    return damageDealt - defenseApplied;
  }


  let Spell = 1
  let Hallucinated = 1
  let Corrupted = 1.2
  let Hardened = 10 

  const damageCapped = () => {
    return Math.min(
      damageReceived(),
      Hardened*Corrupted*Hallucinated+900*(1-Spell)
    );
  }

  const damageInflicted = () => {
    return Math.max(
      .5,
      damageCapped() - guardian
    );
  }

    return (
      <div className="App">
        <header className="App-header">
          <div className={"arena"}>
            <div className={"container"}><Attacker unit={attacker} defender={defender} setOutputDamage={setDamage}/></div>
            <div> Damage Recived = {damageReceived()}</div>
            <div> Damage Capped = {damageCapped()}</div>
            <div> Damage Inflicted = {damageInflicted()}</div>
            <div className={"container"}><Defender unit={defender} setDefense={setDefenseApplied} /></div>
          </div>
        </header>
      </div>
    );
}

export default App;
