import React from 'react';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import {Attacker} from "./components/Attacker"
import {Defender} from "./components/Defender"
import { UnitSelector } from './components/UnitSelector';
function App() {
  const [shieldsResearch, setShieldsResearch] = React.useState(0)
  const [armorResearch, setArmorResearch] = React.useState(0)
  const [attackResearch, setAttackResearch] = React.useState(0);
  const [attack, setAttack] = React.useState(0);
  const [damage, setDamage] = React.useState(null);

  const [guardian, setGuardian] = React.useState(0)

  const [attacker, setAttacker] = React.useState(marauder)
  const [defender, setDefender] = React.useState(immortal)

  const setDefenderShields = (value) => {
    setDefender({
      ...defender,
      base:{
        ...defender.base,
        shields: value
      }
    })
  }
    return (
      <div className="App">
        <header className="App-header">
          <div className={"arena"}>
            <div className={"container"}>
              <Attacker 
                unit={attacker} 
                defender={defender} 
                research={[attackResearch, setAttackResearch]}
                attackChoice={[attack, setAttack]}
                damage={damage}/>
                <UnitSelector setUnit={setAttacker} currentUnit={attacker}/>
            </div>
            <div>
              <Damage 
                attacker={attacker} 
                defender={defender} 
                attack={attack}
                research={[attackResearch, armorResearch, shieldsResearch]}
                setDamage={setDamage}/>
            </div>
            <div className={"container"}>
              <Defender 
                unit={defender} 
                updaters={[shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch]}
                changeStats={[setDefenderShields]}
              />
              <UnitSelector setUnit={setDefender} currentUnit={defender}/>
            </div>
          </div>
        </header>
      </div>
    );
}

const Damage = ({attacker, defender, attack, research, setDamage, guardian=0}) => {
  const [attackResearch, armorResearch, shieldsResearch] = research
  let Spell = 1
  let Hallucinated = 1
  let Corrupted = 1.2
  let Hardened = 900
  let Prismatic = 1

  const bonusDamage = (att, def) => {
    return att.attacks[attack].bonuses.filter(bonus => def.types.includes(bonus.to)).reduce((total, amount) => {
      return total+amount.baseDamage+amount.researchBonus*attackResearch
    }, 0)
  }

  const damageDealt = (att, def) => {
    return (
      att.attacks[attack].baseDamage + 
      attackResearch*att.attacks[attack].researchBonus + 
      bonusDamage(att, def)
    )
  }

  React.useEffect(() => {
    setDamage(damageDealt(attacker, defender));
  }, [attacker, defender, damageDealt, setDamage])

  const armorDefense = (unit) => {
    return unit.base.armor + armorResearch
  }

  const defenseAvailable = (unit) => {
    if(unit.base?.shields > 0){
      return shieldsResearch
    } else {
      return armorDefense(unit)
    }
  }

  const defenseApplied = (unit) => {
    return defenseAvailable(unit)*Spell*(2-Hallucinated)
  }

  const damageReceived = (att, def) => {
    return damageDealt(att, def)*Corrupted*Hallucinated*Prismatic - defenseApplied(def);
  }


  const damageCapped = (att, def) => {
    return Math.min(
      damageReceived(att, def),
      Hardened*Corrupted*Hallucinated+900*(1-Spell)
    );
  }

  const damageInflicted = (att, def) => {
    return Math.max(
      .5,
      damageCapped(att, def) - guardian
    );
  }

  const totalDamage = (att, def) => {
    if(def.base.shields && damageInflicted(att, def) > def.base.shields + armorDefense(def)){
      console.log('def', def)
      return damageInflicted(att, def)-armorDefense(def)
    } else {
      return damageInflicted(att, def)
    }
  }

  const eliminate = (att, def) => {
    const att2 =JSON.parse(JSON.stringify(att))// Object.assign({}, att);
    const def2 = JSON.parse(JSON.stringify(def))//Object.assign({}, def);
    let shots = 0;
    while(def2.base.health > 0){
      console.log('remainning strength', def2.base.shields, def2.base.health)
      let damage = totalDamage(att2, def2);
      shots ++;
      if(def2.base?.shields > 0){
        if(damage <= def2.base.shields){
          def2.base.shields -= damage
        } else {
          damage -= def2.base.shields;
          def2.base.shields = 0;
          def2.base.health -= damage;
        }
      } else {
        def2.base.health -= damage;
      }
    }
    return shots
  }


  return <>
  {defender.base.shields}
    <div> Damage Dealt = {damageDealt(attacker, defender)}</div>
    <div> D Availebl = {defenseAvailable(defender)}</div>
    <div> Damage Recived = {damageReceived(attacker, defender)}</div>
    <div> Damage Capped = {damageCapped(attacker, defender)}</div>
    <div> Damage Inflicted = {damageInflicted(attacker, defender)}</div>
    <div> Total Damage = {totalDamage(attacker, defender)}</div>
    <div> Shots to Kill = {eliminate(attacker, defender)}</div>
  </>
}

export default App;
