import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"

const Bonus = ({attack, defender, style}) => {
  return attack.bonuses.map((bonus, index) => {
    return <>
      <div key={index}>
        anti-{bonus.to}
        {style === "main" ?
        defender.types.includes(bonus.to) ?
        `| ${bonus.baseDamage} + (${bonus.researchBonus})`: ""
      : ""}
      </div>
    </>;
  })
}

const Attacks = ({unit, defender, defaultIndex, setDefaultIndex}) => {
  return unit.attacks.map((attack, index) => {
    const style = index === defaultIndex ? "main" : "default";
    return <div key={index} onClick={() => setDefaultIndex(index)} className={style}>
      <>
        {attack.baseDamage} + ({unit.research.attack})
        <Bonus attack={attack} defender={defender} style={style}/>
      </>
    </div>
  })
}


export const Attacker = ({unit, defender, defaultAttackResearch=0, defaultAttackIndex=0}) => {
  const [attackResearch, setAttackResearch] = React.useState(defaultAttackResearch);
  const [attack, setAttack] = React.useState(defaultAttackIndex);

  const bonusDamage = () => {
    return unit.attacks[attack].bonuses.filter(bonus => defender.types.includes(bonus.to)).reduce((total, amount) => {
      return total+amount.baseDamage+amount.researchBonus*attackResearch
    }, 0)
  }

  return <div className={"unit"}>
    <div>
      <Unit type={unit} />
      <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={defender}/>
    </div>
    <div className={"upgrades"}>
      <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
      {unit.attacks[attack].baseDamage + attackResearch*unit.attacks[attack].researchBonus + bonusDamage()}
    </div>
  </div>
}
