import React from 'react';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import {Attacker} from "./components/Attacker"
import {Defender} from "./components/Defender"
import { UnitSelector } from './components/UnitSelector';
function App() {
  const [shieldsResearch, setShieldsResearch] = React.useState(0)
  const [armorResearch, setArmorResearch] = React.useState(0)
  const [attackResearch, setAttackResearch] = React.useState(0);
  const [attack, setAttack] = React.useState(null);
  const [damage, setDamage] = React.useState(null);


  const [guardian, setGuardian] = React.useState(0)

  const [attacker, setAttacker] = React.useState(marauder)
  const [defender, setDefender] = React.useState(immortal)

  const setDefenderShields = (value) => {
    setDefender({
      ...defender,
      base:{
        ...defender.base,
        shields: value
      }
    })
  }

  React.useEffect(() => {
    setAttack(null)
    setDamage(null)
  }, [attacker])

    return (
      <div className="App">
        <div className={"arena"}>
          <div className={"container"}>
            <Attacker 
              unit={attacker} 
              defender={defender} 
              research={[attackResearch, setAttackResearch]}
              attackChoice={[attack, setAttack]}
              damage={damage}
              setDamage={setDamage}/>
              <UnitSelector setUnit={setAttacker} currentUnit={attacker}/>
              </div>
            <div className={"container"}>
            <Defender 
              unit={defender} 
              updaters={[shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch]}
              changeStats={[setDefenderShields]}
            />
            <UnitSelector setUnit={setDefender} currentUnit={defender}/>
            </div>
        </div>
      </div>
    );
}



export default App;
