import React from "react"

export const useUnits = (checkAgain) => {
  const [response, setResponse] = React.useState(null)
  const [locked, setLocked] = React.useState(false)

  const getUnits = async () =>{
    if(locked) return null
      fetch('http://localhost:5000/api/units')
        .then(res => res.json())
        .then(res =>  {
          setResponse(res.units)
          setLocked(true)
        })
        .catch(err => {
          console.error("error fetching", err)
        })
  }
  React.useEffect(() => {
    getUnits()
  }, [])

  return [locked, response]
}

export const Unit = ({setUnit, unit, weaknesses=[]}) => {
  //<UnitSelector setUnit={setDefender} currentUnit={defender}/> 
  const unitCard = unit? <div className={"unit-card"}>
          <div className={"unit-name"}>{unit.name}</div>
          <div className={"unit-profile"}>
            <img src={unit.img} alt={`portrait for ${unit.name}`}></img>
          </div>
          <div className={"attributes"}>
          {unit.types.map((( type, index ) => {
              return <div key = {index}> 
              {type}
              </div>
            }))}
          </div>
        </div>  
      : null;

  return <>{unitCard}</>

}

export const UnitImg = ({unit, label}) => {
  return <div className={"research"}>
    <img style={{minWidth: "100px"}} src={unit.img} alt={`portrait for ${unit.name}`}></img>
    <div className={"researchValue"}>
      <span>{label}</span>
    </div>
  </div>
}
