import React from 'react';
import logo from './logo.svg';
import { Counter } from './features/counter/Counter';
// import { Upgrade } from './features/upgrade/Upgrade';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import styles from './features/counter/Counter.module.css';


const researchSrcs = {
  attack: "/attack.png", 
  armor: "/armor.png",
  shields: "/shield.png"
};

function App() {
  const Unit = ({type}) => {

    return <div className={"card-image"}>
      <img src={type.img}></img>
      🧊: {type.base.shields}
      ❤️: {type.base.health}
    </div>
  }

  const Upgrade = ({researchKind, value})=> {
    const [level, setLevel] = React.useState(value)

    const incrementByAmount = (amount) => {
      setLevel(level + amount)
    }


    if(value === null){
      return <></>
    }


    return <>
    <div>
      <img src = {researchSrcs[researchKind]} ></img>
      <div className={styles.row}>
        <button
          className={styles.button}
          aria-label="Decrement value"
          onClick={() => (incrementByAmount(-1))}
        >
          -
        </button>
        <span className={styles.value}>{level}</span>
        <button
          className={styles.button}
          aria-label="Increment value"
          onClick={() => (incrementByAmount(1))}
        >
          +
        </button>
      </div>
    </div> </>
  }

  const Bonus = ({attack}) => {
    return attack.bonuses.map((bonus, index) => {
      return <div key={index}>anti-{bonus.to}</div>
    })
  }

  const Attacks = ({unit, defaultIndex, setDefaultIndex}) => {
    return unit.attacks.map((attack, index) => {
      const style = index === defaultIndex ? "main" : "default";
      return <div key={index} onClick={() => setDefaultIndex(index)} className={style}>
        <>{attack.baseDamage} + ({unit.research.attack}) <Bonus attack={attack} /></>
      </div>
    })
  }

  const Attacker = ({unit, defaultAttackResearch=0, defaultAttackIndex=0}) => {
    const [attackResearch, setAttackResearch] = React.useState(defaultAttackResearch);
    const [attack, setAttack] = React.useState(defaultAttackIndex);

    return <div className={"unit"}>
      <div>
        <Unit type={unit} />
        <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack}/>
      </div>
      <div className={"upgrades"}>
        <Upgrade researchKind={"attack"} value={attackResearch} />
      </div>
    </div>
  }

  const Defender = ({unit}) => {
    const [shieldsResearch, setShieldsResearch] = React.useState(unit.research.shields ? 0: null)
    const [armorResearch, setArmorResarch] = React.useState(0)

    return <div className={"unit"}>
      <div>
        <Unit type={unit} />
      </div>
      <div className={"upgrades"}>
        <Upgrade researchKind={"shields"} value={shieldsResearch} />
        <Upgrade researchKind={"armor"} value={armorResearch} />
      </div>
    </div>
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className={"arena"}>
          <div className={"container"}><Attacker unit={marauder}/></div>
          <div> middle ground</div>
          <div className={"container"}><Defender unit={immortal} /></div>
        </div>
      </header>
    </div>
  );
}

export default App;
