import React from "react"
import {units} from "../SC2Data"

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      factory: "/factory.png",
      barracks: "/barracks.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

export const UnitSelector = () => {
  const Production = ({structure, path}) => {
    const [hidden, setHidden] = React.useState(false)
    const subUnits = units.filter(unit => unit.structure === structure)
    let display;

    if(hidden){
      display = <></>
    } else {
      display = <>
      {subUnits.map((unit,index) => {
        <div key={index} onClick={setActiveUnit(unit)}>
          {unit.name}
        </div>
      })}
      </>
    }

    return <>
    <img src={path} onClick={() => setHidden(!hidden)}></img>
    {display}
    </>
  }

  const Faction = ({data}) => {
    const [hidden, setHidden] = React.useState(false)

    return <>
      <img src={data.icon} onClick={() => setHidden(!hidden)}></img>
        {hidden ? "" : 
        Object.entries(data.production).map(([structure, path]) => {
          return (
            <Production key={structure} structure={structure} path={path} />
          );
        })
      }
      </>
  }

  return Object.entries(factions).map( ([key, value]) => {
    return (
      <div key={key}>
        <Faction data={value} />
      </div>
    );
  })


}
