import React from 'react';
import logo from './logo.svg';
import { Counter } from './features/counter/Counter';
// import { Upgrade } from './features/upgrade/Upgrade';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import styles from './features/counter/Counter.module.css';


const researchSrcs = {
  attack: "/attack.png", 
  armor: "/armor.png",
  shields: "/shield.png"
};

function App() {
  const Unit = ({type}) => {

    return <div className={"container"}>

        <img src={type.img}></img>
        <div className={"unit-stats"}>
          {type.base.shields ?
            <div>🧊: {type.base.shields}</div>:
            ""
          }
          <div>❤️: {type.base.health}</div>
      </div>
    </div>
  }

  const Upgrade = ({researchKind, value, updateResearch})=> {
    const incrementByAmount = (amount) => {
      updateResearch(value + amount)
    }

    if(value === null){
      return <></>
    }


    return <>
    <div>
      <img src = {researchSrcs[researchKind]} ></img>
      <div className={styles.row}>
        <button
          className={styles.button}
          aria-label="Decrement value"
          onClick={() => (incrementByAmount(-1))}
        >
          -
        </button>
        <span>{value}</span>
        <button
          className={styles.button}
          aria-label="Increment value"
          onClick={() => (incrementByAmount(1))}
        >
          +
        </button>
      </div>
    </div> </>
  }

  const Bonus = ({attack}) => {
    return attack.bonuses.map((bonus, index) => {
      return <div key={index}>anti-{bonus.to} | {bonus.baseDamage} + ({bonus.researchBonus})</div>
    })
  }

  const Attacks = ({unit, defender, defaultIndex, setDefaultIndex}) => {
    return unit.attacks.map((attack, index) => {
      const style = index === defaultIndex ? "main" : "default";
      return <div key={index} onClick={() => setDefaultIndex(index)} className={style}>
        <>
          {attack.baseDamage} + ({unit.research.attack}) 
          <Bonus attack={attack} />
          {defender.types}
        </>
      </div>
    })
  }

  const Attacker = ({unit, defender, defaultAttackResearch=0, defaultAttackIndex=0}) => {
    const [attackResearch, setAttackResearch] = React.useState(defaultAttackResearch);
    const [attack, setAttack] = React.useState(defaultAttackIndex);

    return <div className={"unit"}>
      <div>
        <Unit type={unit} />
        <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={Defender}/>
      </div>
      <div className={"upgrades"}>
        <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
        {unit.attacks[attack].baseDamage + attackResearch*unit.attacks[attack].researchBonus} 
      </div>
    </div>
  }

  const Defender = ({unit}) => {
    const [shieldsResearch, setShieldsResearch] = React.useState(0)
    const [armorResearch, setArmorResearch] = React.useState(0)

    return <div className={"unit"}>
      <div>
        <Unit type={unit} />
      </div>
      <div className={"upgrades"}>
        {unit.research.shields? 
          <div>
            <Upgrade researchKind={"shields"} updateResearch={setShieldsResearch} value={shieldsResearch} />
            🧊 {shieldsResearch*unit.research.shields}
          </div> :
          <></>
        }

        <div>
          <Upgrade researchKind={"armor"} updateResearch={setArmorResearch} value={armorResearch} />
          🛡 {unit.base.armor + armorResearch*unit.research.armor}
        </div>

      </div>
    </div>
  }

const attacker = marauder;
const defender = immortal

  return (
    <div className="App">
      <header className="App-header">
        <div className={"arena"}>
          <div className={"container"}><Attacker unit={attacker} defender={defender}/></div>
          <div> middle ground</div>
          <div className={"container"}><Defender unit={defender} /></div>
        </div>
      </header>
    </div>
  );
}

export default App;
