import React from "react"
import {units} from "../SC2Data"

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      factory: "/factory.png",
      barracks: "/barracks.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

export const UnitSelector = () => {
  const Production = ({structure}) => {
    return <img src={structure}></img>
  }

  const Faction = ({data}) => {
    const [hidden, setHidden] = React.useState(true)

    return <>
      <img src={data.icon} onClick={() => setHidden(!hidden)}></img>
        {hidden ? "" : 
        Object.values(data.production).map((structure,idx) => {
          return (
            <Production key={idx} structure={structure} />
          );
        })
      }
      </>
  }

  return Object.entries(factions).map( ([key, value]) => {
    return (
      <div key={key}>
        <Faction data={value} />
      </div>
    );
  })


}
