import React from 'react';
import {marauder, immortal, units} from "./SC2Data"
import './App.css';
import {Attacker} from "./components/Attacker"
import {Defender} from "./components/Defender"
import {Upgrade} from "./components/Upgrade"
import { UnitSelector } from './components/UnitSelector';


function App() {
  const [shieldsResearch, setShieldsResearch] = React.useState(0)
  const [armorResearch, setArmorResearch] = React.useState(0)
  const [attackResearch, setAttackResearch] = React.useState(0);

  const [attackIndex, setAttackIndex] = React.useState(null)

  const [attacker, setAttacker] = React.useState(null)
  const [defender, setDefender] = React.useState(null)

  const setDefenderShields = (value) => {
    setDefender({
      ...defender,
      base:{
        ...defender.base,
        shields: value
      }
    })
  }

  React.useEffect(() => {
    setAttackIndex(attackIndex)
  }, [attackIndex])

  const RandomButton = ({ position }) => {
    const randomUnit = units[Math.floor(Math.random() * units.length)]

    return <div className={"random-button"}>
    <button onClick={() => position(randomUnit)}>random : {randomUnit.name}</button>
      </div>
  }

  const attack_info = attacker ? 
    <>
    <Attacker 
      unit={attacker} 
      defender={defender} 
      attackChoice={[attackIndex, setAttackIndex]}/>
      </>
    : <RandomButton position={setAttacker} /> ;

  const defender_info = defender ?
  <>
  <Defender 
    unit={defender} 

    changeStats={[setDefenderShields]}
  />
  </> : <RandomButton position={setDefender} /> ;

    return (
      <div className="App">
        <div className={"arena"}>
          <div className={"container"}>
            {attack_info}
            <UnitSelector setUnit={setAttacker} currentUnit={attacker}/>
          </div>
          <div className={"container"}>
            <Middle 
              attacker={attacker}
              defender={defender}
              research={[attackResearch, setAttackResearch, shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch]}
            />
          </div>
          <div className={"container"}>
            {defender_info}
            <UnitSelector setUnit={setDefender} currentUnit={defender}/>
          </div>
        </div>
      </div>
    );
}

const Middle = ({attacker, defender, research}) => {
  if(!research){
    return null;
  }
  const [attackResearch, setAttackResearch, shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch] = research
  const upgrades =  <div className={"upgrades"}>
    <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
    {defender.research.shields?
        <Upgrade researchKind={"shields"} updateResearch={setShieldsResearch} value={shieldsResearch} />:
      null
    }
    <Upgrade researchKind={"armor"} updateResearch={setArmorResearch} value={armorResearch} />
  </div>
  if(attacker && defender){
    return <>attacker found?</>
  } else {
    return "waiting"
  }
  // console.log('attacker', attacker)
  // console.log('defender', defender)
  // console.log('research', research)

  // return(

  //   );
}


export default App;
