import React from 'react';
import logo from './logo.svg';
import { Counter } from './features/counter/Counter';
// import { Upgrade } from './features/upgrade/Upgrade';
import {marauder, immortal} from "./SC2Data"
import './App.css';
import styles from './features/counter/Counter.module.css';


const researchSrcs = {
  attack: "/attack.png", 
  armor: "/armor.png",
  shields: "/shield.png"
};

function App() {
  // const upgrades = ["/attack.png", "/shield.png", "/armor.png"].map((item, id) => {
  //   return <Upgrade key={id} src={item}/>
  // })

  const Unit = ({type}) => {
    return <>
      <img src={type.img}></img>
    </>
  }

  const Upgrade = ({researchKind, value})=> {
    const [level, setLevel] = React.useState(value)

    const incrementByAmount = (amount) => {
      setLevel(level + amount)
    }


    if(value === null){
      return <></>
    }


    return <>Upgrade {researchKind}
    <div>
      <img src = {researchSrcs[researchKind]} ></img>
      <div className={styles.row}>
        <button
          className={styles.button}
          aria-label="Decrement value"
          onClick={() => (incrementByAmount(-1))}
        >
          -
        </button>
        <span className={styles.value}>{level}</span>
        <button
          className={styles.button}
          aria-label="Increment value"
          onClick={() => (incrementByAmount(1))}
        >
          +
        </button>
      </div>
    </div> </>
  }

  const Attacker = ({unit}) => {
    const [attackResearch, setAttackResearch] = React.useState(0)

    const Attacks = unit.attacks.map((attack, id) => {
      return <div key={id}>
        {attack.targets}
      </div>;
    })
    return <>
      <Unit type={unit} />
      <Upgrade researchKind={"attack"} value={attackResearch} />
    </>
  }

  const Defender = ({unit}) => {
    const [shieldsResearch, setShieldsResearch] = React.useState(unit.research.shields ? 0: null)
    const [armorResearch, setArmorResarch] = React.useState(0)

    return <>
      <Unit type={unit} />
      <Upgrade researchKind={"shields"} value={shieldsResearch} />
      <Upgrade researchKind={"armor"} value={armorResearch} />
    </>
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <Attacker unit={marauder}/>
        <Defender unit={immortal} />

        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <span>
          <span>Learn </span>
          <a
            className="App-link"
            href="https://reactjs.org/"
            target="_blank"
            rel="noopener noreferrer"
          >
            React
          </a>
          <span>, </span>
          <a
            className="App-link"
            href="https://redux.js.org/"
            target="_blank"
            rel="noopener noreferrer"
          >
            Redux
          </a>
          <span>, </span>
          <a
            className="App-link"
            href="https://redux-toolkit.js.org/"
            target="_blank"
            rel="noopener noreferrer"
          >
            Redux Toolkit
          </a>
          ,<span> and </span>
          <a
            className="App-link"
            href="https://react-redux.js.org/"
            target="_blank"
            rel="noopener noreferrer"
          >
            React Redux
          </a>
        </span>
      </header>
    </div>
  );
}

export default App;
