import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"
import { useSpring, animated } from 'react-spring'
import { immortal, marauder, marine } from '../SC2Data'


const DEFENDER_DEAD = "defender is dead"

const Bonus = ({attack, defender, textstyle}) => {

  return attack.bonuses.map((bonus, index) => {
    let baseText = <>Anti-{bonus.to}:</>
    let bonusText = <>{bonus.baseDamage} (+{bonus.researchBonus})</>;
    let modifier;

    if(!defender){
      modifier = <span>{bonusText}</span>
    } else {
      const overlap = intersection(intersection(attack.targets, defender.types), ["Ground", "Air"]);
      if (overlap.length === 0){
        modifier = <span> {bonusText} </span>
      }else{
        if (defender.types.includes(bonus.to) ){
          modifier=  <span>{bonusText} 🎯</span>
        } else {
          modifier= <span style={{textDecoration: "wavy line-through red"}}>{ bonusText}</span>
        }
      }
    }

    return <div key={index}>
      {baseText}
      <span className={textstyle}>
      {modifier}
      </span>
    </div>
  })


}

const Bullet = ({animate, display}) => {
  if(display){
    if (animate){
      return <span className={"bullet"}>➡</span>
    } else {
      return <>➡</>
    }
  } else {
    return <></>
  }
}

function intersection(a, b) {
  var setA = new Set(a);
  var setB = new Set(b);
  var intersection = new Set([...setA].filter(x => setB.has(x)));
  return Array.from(intersection);
}

export const canAttackTargetDefender = (attack, defender) => {

  if(attack && defender){
    const overlap = intersection(intersection(attack.targets, defender.types), ["Ground", "Air"]);
    return overlap.length > 0
  } else {
    return false
  }
}

const Attacks = ({unit, defender, defaultIndex, setDefaultIndex}) => {
  const toggleIndex = (index) => {
      if(index === defaultIndex){
        setDefaultIndex(null)
      } else {
        setDefaultIndex(index)
      }
    }
  return unit.attacks.map((attack, index) => {
    const cns = []
    cns.push(index === defaultIndex ? "main-weapon" : "alt-weapon");
    const available = canAttackTargetDefender(attack, defender);
    cns.push(available ? "attack-available" : "attack-unavailable");
    const cn = available ? "success" : "fail";
    console.log('available', available)

    return <div key={index} onClick={() => toggleIndex(index)} className={cns.join(" ")}>
      <>
        <div className={"attack-name"}><Bullet animate={available && (defaultIndex === null)} display={(defaultIndex !== index)}/>{attack.name}</div>
        {index === defaultIndex ?
          <div>
            <>Base Damage: <span className={cn}>{attack.baseDamage} + ({attack.researchBonus})</span></>
            <Bonus attack={attack} defender={defender} textstyle={cn}/>
          </div>
          : null
          }
      </>
    </div>
  })
}



export const Attacker = ({unit, defender, research, attackChoice}) => {
  const [attack, setAttack] = attackChoice;

  // let damageIndicator;
  // if(unit.attacks.length > 0 ){
  //   if(damage){
  //     damageIndicator = <h1>⚔️ {damage}</h1>;
  //   } else {
  //     damageIndicator = <>⚔️ 👀 </>;
  //   }
  // } else {
  //   damageIndicator = <></>;
  // }

  return <div className={"unit"}>
    <div>
    <Unit unit={unit}>
    </Unit>
      <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={defender} />
    </div>
  </div>
}




// d = Damage(unit, defender, attack, attackResearch, armorResearch, shieldsResearch)
// Damage.oneStep(unit, defender)
export class Damage{
  constructor( attacker, defender, attackIndex, research, guardian=0) {
  const [attackResearch, shieldsResearch, armorResearch] = research
  this.research = {
    attack: attackResearch,
    armor: armorResearch,
    shields: shieldsResearch
  }

  this.att = JSON.parse(JSON.stringify(attacker));
  this.def = JSON.parse(JSON.stringify(defender));
  this.attackIndex = attackIndex

  let Spell = 1
  let Hallucinated = 1
  let Corrupted = 1
  let Hardened = 900
  let Prismatic = 1

  this.bonusDamage = () => {
    return this.att.attacks[this.attackIndex].bonuses.filter(bonus => this.def.types.includes(bonus.to)).reduce((total, amount) => {
      return total+amount.baseDamage+amount.researchBonus*this.research.attack
    }, 0)
  }

  this.damageDealt = () => {
    const attack = this.att?.attacks[this.attackIndex]
    if(this.att && this.def && attack){
      return (
        attack.baseDamage +
        this.research.attack*attack.researchBonus +
        this.bonusDamage()
      )  
    } else {
      return "not enough info"
    }
  }

  this.armorDefense = () => {
    return this.def.base.armor + this.research.armor
  }

  this.defenseAvailable = () => {
    if(this.def.base?.shields > 0){
      return this.research.shields
    } else {
      return this.armorDefense(this.def)
    }
  }

  this.defenseApplied = () => {
    return this.defenseAvailable()*Spell*(2-Hallucinated)
  }

this.damageReceived = () => {
    return this.damageDealt()*Corrupted*Hallucinated*Prismatic - this.defenseApplied();
  }


  this.damageCapped = (att, def) => {
    return Math.min(
      this.damageReceived(att, def),
      Hardened*Corrupted*Hallucinated+900*(1-Spell)
    );
  }

  this.damageInflicted = (att, def) => {
    return Math.max(
      .5,
      this.damageCapped(att, def) - guardian
    );
  }


}

totalDamage = () => {
  const def = this.def
  const att = this.att

  if(def.base.shields && this.damageInflicted(att, def) > def.base.shields + this.armorDefense(def)){
    return this.damageInflicted(att, def)-this.armorDefense(def)
  } else {
    return this.damageInflicted(att, def)
  }
}


eliminate = () => {
  let newValues = this.oneShot();
  let count = 1
  while(newValues?.health > 0){
    count++;
    for(var i = 0; i < this.att.attacks[this.attackIndex].repeats; i++){
      if(newValues){
        const defender = {
          ...this.def,
          base: {
            ...this.def.base,
            health: newValues.health,
            shields: newValues.shields,
          }
        }
        const d =  new Damage(this.att,
          JSON.parse(JSON.stringify(defender)), this.attackIndex, 
          [ this.research.attack, this.research.shields, this.research.armor])
        newValues= d.oneShot()
      }
      }
    }
    return count;
}


oneShot = (faked=true) => {
  let att; let def;
  if(faked){
    att = JSON.parse(JSON.stringify(this.att));
    def = JSON.parse(JSON.stringify(this.def));
  } else {
    att = this.att
    def = this.def
  }

  let results = null
  let log;

  if(att?.attacks[this.attackIndex] === undefined || !def){
    return "👀"
  } if(
    !canAttackTargetDefender(att.attacks[this.attackIndex], def)
  ){
    return "can't shoot"
  }
  if(def.base.health > 0){
    let totalDamage=0;

    let damage = this.totalDamage();
    for(var i = 0; i < att.attacks[this.attackIndex].repeats; i++){
      if(def.base?.shields > 0){
        if(damage <= def.base.shields){
          def.base.shields -= damage
          log = `${damage} to shields. (${def.base.shields} remaining)`
        } else {
          damage -= def.base.shields;
          def.base.shields = 0;
          def.base.health -= damage;
          log = `${damage} has overloaded shields. (${def.base.health} health remaining)`
        }
      } else {
        def.base.health -= damage;
        log = `${damage} damage. (${def.base.health} HP)`
      }
      totalDamage += damage;
      if(def.base.health < 0){
        const overkill = (100*-def.base.health/totalDamage).toFixed(1)
        log += ` (${overkill}% attack wasted)`
      }
      results = ({health: def.base.health, shields: def.base.shields, totalDamage: totalDamage})
    }
  }

  return results
}
}
