import React from "react"
import {units} from "../SC2Data"
import { useSpring, animated } from 'react-spring'

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      barracks: "/barracks.png",
      factory: "/factory.png",
      starport: "/starport.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

const camel2str = (str) => {
   // adding space between strings
  const result = str.replace(/([A-Z])/g,' $1');

  // converting first character to uppercase and join it to the final string
  const final = result.charAt(0).toUpperCase()+result.slice(1);
  return final
}

export const UnitSelector = ({setUnit, currentUnit}) => {
  const [searchText, setSearchText] = React.useState("")
  const input = <input type={"search"} placeholder={"...unit name"} onChange={(e) => setSearchText(e.target.value)} value={searchText}/>

  const showUnits = (subUnits) => {
    const classes = ["unit-selection"]

    // activeUnit = ? "#367180": "#";
    return subUnits.map((unit,index) => {
      let inlineStyle = {
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'right',
        backgroundImage: `url(${ unit.img})`,
      };
      if( currentUnit?.name === unit.name){
        classes.push("active-unit")
      }
      return <div key={index} onClick={() => setUnit(unit)} className={classes.join(" ")} style={inlineStyle}>
        <header><h1>{unit.name}</h1></header>
      </div>
    })
  }

  const Production = ({structure, path, animations}) => {
    const [hidden, setHidden] = React.useState(false)
    const subUnits = units.filter(unit => unit.structure === structure)

    let display = hidden? <></> :
      <div className={"units-selector"}>{showUnits(subUnits)}</div>;

    let inlineStyle = {
      backgroundSize: 'contain',
      backgroundRepeat: 'no-repeat',
      backgroundImage: `url(${ path})`,
    };

    return <div className={"production-facility"}>

        <div className={"unit-selection"} style={inlineStyle} onClick={() => setHidden(!hidden)}>
        < header><h1>{camel2str(structure)}</h1></header>
        </div>


    </div>
  }

  const Faction = ({data, label}) => {
    const [hidden, setHidden] = React.useState(false);

    const imgBackground = ({
      background: hidden ?  "#ebebeb" : null,
      width:"100%",
    })

    return <div style={imgBackground} >

      <div onClick={() => setHidden(!hidden)}>
        <div>{label}</div>
        <img src={data.icon} ></img>
        </div>
        {hidden ? null : 
        <div>
          {
            Object.entries(data.production).map(([structure, path]) => {
              return <Production 
                        key={structure} 
                        structure={structure} 
                        path={path} />
            })
          }
        </div>
      }
      </div>
  }

  if(searchText.length > 0){
    let subUnits = units.filter(unit => unit.name.toLowerCase().includes(searchText.toLowerCase()))
    return <>
      {input}
      {showUnits(subUnits)}
    </>
  } else {
    return <>
      {input}
      {Object.entries(factions).map( ([key, value]) => {
      return (
        <div key={key} className={"faction"}>
          <Faction data={value} label={key} />
        </div>
      );
    })}
    </>
  }




}
