import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"
import { useSpring, animated } from 'react-spring'


const Bonus = ({attack, defender, style}) => {
  return attack.bonuses.map((bonus, index) => {
    let bonusText = ` +${bonus.baseDamage} + (${bonus.researchBonus})`;

    return <>
      <div key={index}>
        Anti-{bonus.to}: 
        {defender.types.includes(bonus.to) ?
        <span className={style}>
          {bonusText}
        </span>: 
        <span style={{textDecoration: "wavy line-through red"}}>{ bonusText}</span>
        }
      </div>
    </>;
  })
}

const Bullet = ({animate, display}) => {
  if(display){
    if (animate){
      return <span className={"bullet"}>‚û°</span>
    } else {
      return <>‚û°</>
    }
  } else {
    return <></>
  }
}

function intersection(a, b) {
  var setA = new Set(a);
  var setB = new Set(b);
  var intersection = new Set([...setA].filter(x => setB.has(x)));
  return Array.from(intersection);
}

const canAttackTargetDefender = (attack, defender) => intersection(intersection(attack.targets, defender.types), ["Ground", "Air"]).length > 0

const Attacks = ({unit, defender, defaultIndex, setDefaultIndex, setDamage}) => {
  const toggleIndex = (index) => {
      if(index === defaultIndex){
        setDefaultIndex(null)
        setDamage(null)
      } else {
        setDefaultIndex(index)
      }
    }
  return unit.attacks.map((attack, index) => {
    const style = index === defaultIndex ? "main-weapon" : "alt-weapon";
    const available = canAttackTargetDefender(attack, defender)
    const cn = available ? "success" : "fail";

    return <div key={index} onClick={() => toggleIndex(index)} className={style}>
      <>
        <div className={"attack-name"}><Bullet animate={defaultIndex === null} display={available && (defaultIndex !== index)}/>{attack.name}</div>
        {index === defaultIndex ?
          <div>
            <>Base Damage: <span className={cn}>{attack.baseDamage} + ({attack.researchBonus})</span></>
            <Bonus attack={attack} defender={defender} style={cn}/>
          </div>
          : null
          }
      </>
    </div>
  })
}



export const Attacker = ({unit, defender, research, attackChoice, damage, setDamage}) => {
  const [attack, setAttack] = attackChoice;
  const [attackResearch, setAttackResearch] = research;

  let damageIndicator;
  if(unit.attacks.length > 0 ){
    if(damage){
      damageIndicator = <h1>‚öîÔ∏è {damage}</h1>;
    } else {
      damageIndicator = <>‚öîÔ∏è üëÄ </>;
    }
  } else {
    damageIndicator = <></>;
  }


  Damage.oneStep(unit, defender)

  return <div className={"unit"}>
    <div>
    <Unit type={unit}>
      {damageIndicator}
    </Unit>
      <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={defender} setDamage={setDamage}/>
    </div>
    <div className={"upgrades"}>
      <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
    </div>
  </div>
}

class Damage{
  constructor( attacker, defender, attack, research, setDamage, guardian=0) {



  const bonusDamage = (att, def) => {
    return att.attacks[attack].bonuses.filter(bonus => def.types.includes(bonus.to)).reduce((total, amount) => {
      return total+amount.baseDamage+amount.researchBonus*attackResearch
    }, 0)
  }

  const damageDealt = (att, def) => {
    return (
      att.attacks[attack].baseDamage + 
      attackResearch*att.attacks[attack].researchBonus + 
      bonusDamage(att, def)
    )
  }

  React.useEffect(() => {
    if(attacker.attacks[attack]) {setDamage(damageDealt(attacker, defender))}
  }, [attacker, defender, damageDealt, setDamage])

  const armorDefense = (unit) => {
    return unit.base.armor + armorResearch
  }

  const defenseAvailable = (unit) => {
    if(unit.base?.shields > 0){
      return shieldsResearch
    } else {
      return armorDefense(unit)
    }
  }

  const defenseApplied = (unit) => {
    return defenseAvailable(unit)*Spell*(2-Hallucinated)
  }

  const damageReceived = (att, def) => {
    return damageDealt(att, def)*Corrupted*Hallucinated*Prismatic - defenseApplied(def);
  }


  const damageCapped = (att, def) => {
    return Math.min(
      damageReceived(att, def),
      Hardened*Corrupted*Hallucinated+900*(1-Spell)
    );
  }

  const damageInflicted = (att, def) => {
    return Math.max(
      .5,
      damageCapped(att, def) - guardian
    );
  }

  const totalDamage = (att, def) => {
    if(def.base.shields && damageInflicted(att, def) > def.base.shields + armorDefense(def)){
      console.log('def', def)
      return damageInflicted(att, def)-armorDefense(def)
    } else {
      return damageInflicted(att, def)
    }
  }

  function oneStep(att, def) {
    const att2 =JSON.parse(JSON.stringify(att))// Object.assign({}, att);
    const def2 = JSON.parse(JSON.stringify(def))//Object.assign({}, def);
    const results = []
    let log;

    if(att2.attacks[attack] === undefined){
      return "üëÄ"
    }
    if(def2.base.health > 0){
      let damage = totalDamage(att2, def2);
      for(var i = 0; i < att2.attacks[attack].repeats; i++){
        if(def2.base?.shields > 0){
          if(damage <= def2.base.shields){
            def2.base.shields -= damage
            log = `${damage} to shields. (${def2.base.shields} remaining)`
          } else {
            damage -= def2.base.shields;
            def2.base.shields = 0;
            def2.base.health -= damage;
            log = `${damage} has overloaded shields. (${def2.base.health} health remaining)`
          }
        } else {
          def2.base.health -= damage;
          log = `${damage} damage. (${def2.base.health} HP)`
        }
        results.push(log)
      }
    }
    return results
  }

  // const eliminate = (att, def) => {
  //   const att2 =JSON.parse(JSON.stringify(att))// Object.assign({}, att);
  //   const def2 = JSON.parse(JSON.stringify(def))//Object.assign({}, def);
  //   const results = []

  //   if(att2.attacks[attack] === undefined){
  //     return "üëÄ"
  //   }
  //   let shots = 0;
  //   while(def2.base.health > 0){
  //     let damage = totalDamage(att2, def2);
  //     results.push([def2.base.shields, def2.base.health, "|", damage])
  //     shots ++;
  //     for(var i = 0; i < att2.attacks[attack].repeats; i++){
  //       if(def2.base?.shields > 0){
  //         if(damage <= def2.base.shields){
  //           def2.base.shields -= damage
  //         } else {
  //           damage -= def2.base.shields;
  //           def2.base.shields = 0;
  //           def2.base.health -= damage;
  //         }
  //       } else {
  //         def2.base.health -= damage;
  //       }
  //     }
  //   }
  //   return shots
  // }

  // const res = eliminate(attacker, defender);
  // if(Number.isInteger(res)){
  //   return <div style={{width: "200px"}}>
  //           {defender.base.shields}
  //             <div> Shots to Kill = {}</div>
  //           </div>
  // } else {
  //   return <>{res}</>;
  // }

}
}
