export const Unit = ({type, stats}) => {

  return <div className={"container"}>

      <img src={type.img} alt={`portrait for ${type.name}`}></img>
      {stats? <div className={"unit-stats"}>
        {type.base.shields ?
          <div>🧊: {type.base.shields}</div>:
          ""
        }
        <div>❤️: {type.base.health}</div>
        <div>🛡: {type.base.armor}</div>
    </div> : null}

  </div>
}
