import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"
import { useSpring, animated } from 'react-spring'


const Bonus = ({attack, defender, style}) => {
  return attack.bonuses.map((bonus, index) => {
    let bonusText = ` +${bonus.baseDamage} + (${bonus.researchBonus})`;

    return <>
      <div key={index}>
        Anti-{bonus.to}: 
        {defender.types.includes(bonus.to) ?
        <span className={"damage"}>
          {bonusText}
        </span>: 
        <span style={{textDecoration: "wavy line-through red"}}>{ bonusText}</span>
        }
      </div>
    </>;
  })
}

const Bullet = ({animate, display}) => {

  const springProps = useSpring({
    from: { color: "white" },
    to: { color: "red" }
  })

  if(display){
    if (animate){
      return <animated.span className={"bullet"} style={springProps}>➡ animated</animated.span>
    } else {
      return <>➡</>
    }
  } else {
    return <></>
  }
}

const Attacks = ({unit, defender, defaultIndex, setDefaultIndex}) => {
  const toggleIndex = (index) => {
    if(index === defaultIndex){
      setDefaultIndex(null)
    } else {
      setDefaultIndex(index)
    }

  }
  return unit.attacks.map((attack, index) => {
    const style = index === defaultIndex ? "main-weapon" : "alt-weapon";
    return <div key={index} onClick={() => toggleIndex(index)} className={style}>
      <>
        <div className={"attack-name"}><Bullet animate={defaultIndex === null} display={defaultIndex !== index}/>{attack.name}</div>
        {index === defaultIndex ?
          <div>
            <>Base Damage: <span className={"damage"}>{attack.baseDamage} + ({attack.researchBonus})</span></>
            <Bonus attack={attack} defender={defender} style={style}/>
          </div>
          : null
          }
      </>
    </div>
  })
}


export const Attacker = ({unit, defender, research, attackChoice, damage}) => {
  const [attack, setAttack] = attackChoice;
  const [attackResearch, setAttackResearch] = research;



  return <div className={"unit"}>
    <div>
      <Unit type={unit}>
        <h1>⚔️ {damage}</h1>
      </Unit>
      <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={defender}/>
    </div>
    <div className={"upgrades"}>
      <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
    </div>
  </div>
}
