import React from "react"
import {units} from "../SC2Data"
import { useSpring, animated } from 'react-spring'

const factions = {
  terran: {
    icon: "/marine.png",
    production: {
      barracks: "/barracks.png",
      factory: "/factory.png",
    }
  },
  zerg: {
    icon: "/zergling.png",
    production: {

    }
  },
  protoss: {
    icon: "/zealot.png",
    production: {
      roboticsFacility: "/robo.png"
    }
  }
}

const camel2str = (str) => {
   // adding space between strings
  const result = str.replace(/([A-Z])/g,' $1');

  // converting first character to uppercase and join it to the final string
  const final = result.charAt(0).toUpperCase()+result.slice(1);
  return final
}

export const UnitSelector = ({setUnit, currentUnit}) => {
  const [searchText, setSearchText] = React.useState("")
  const input = <input onChange={(e) => setSearchText(e.target.value)} value={searchText}/>

  const showUnits = (subUnits) => {
    return subUnits.map((unit,index) => {
      let inlineStyle = {
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'right',
        backgroundImage: `url(${ unit.img})`,
        backgroundColor: currentUnit.name === unit.name ? "green": "red"};
      return <div key={index} onClick={() => setUnit(unit)} className={"unit-selection"} style={inlineStyle}>
        <header><h1>{unit.name}</h1></header>
      </div>
    })
  }

  const Production = ({structure, path, animations}) => {
    const [hidden, setHidden] = React.useState(true)
    const subUnits = units.filter(unit => unit.structure === structure)

    const menuAppear = useSpring({
      transform: !hidden ? "translate3D(0,0,0)" : "translate3D(-40px,-40px,0)",
      opacity: hidden ? 0 : 1,
     })

    const imgBackground = useSpring({
      background: hidden ?  "#ebebeb" : "#6ce2ff",
    })

    let display;

    if(hidden){
      display = <></>
    } else {
      display = <div className={"units-selector"}>
      {showUnits(subUnits)}
      </div>
    }


    let inlineStyle = {
      backgroundSize: 'contain',
      backgroundRepeat: 'no-repeat',
      backgroundImage: `url(${ path})`};

    return <>
    <animated.div style={imgBackground}>
      <div className={"unit-selection"} style={inlineStyle} onClick={() => setHidden(!hidden)}>
      <header><h1>{camel2str(structure)}</h1></header>
      </div>
      </animated.div>
    <animated.div style={menuAppear}>{display}</animated.div>
    </>
  }

  const Faction = ({data}) => {
    const [hidden, setHidden] = React.useState(true);
    const menuAppear = useSpring({
      transform: !hidden ? "translate3D(0,0,0)" : "translate3D(-400px,-40px,0)",
      opacity: hidden ? 0 : 1,
     })

    const imgBackground = useSpring({
      background: hidden ?  "#ebebeb" : "#6ce2ff",
      width:"100%",
    })

    return <animated.div style={imgBackground} >
      <div onClick={() => setHidden(!hidden)}><img src={data.icon} ></img></div>

        {hidden ? null : 
        <animated.div style={menuAppear}>
          {
            Object.entries(data.production).map(([structure, path]) => {
              return <Production 
                        key={structure} 
                        structure={structure} 
                        path={path} />
            })
          }
        </animated.div>
      }
      </animated.div>
  }

  if(searchText.length > 0){
    let subUnits = units.filter(unit => unit.name.toLowerCase().includes(searchText.toLowerCase()))
    return <>
      {input}
      {showUnits(subUnits)}
    </>
  } else {
    return <>
      {input}
      {Object.entries(factions).map( ([key, value]) => {
      return (
        <div key={key} className={"faction"}>
          <Faction data={value} />
        </div>
      );
    })}
    </>
  }




}
