import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"

export const Defender = ({unit, setDefense}) => {
    const [shieldsResearch, setShieldsResearch] = React.useState(0)
    const [armorResearch, setArmorResearch] = React.useState(0)

    React.useEffect(() => {
      const defenseAvailable = () => {
        if(unit.base?.shields > 0){
          return shieldsResearch
        } else {
          return unit.base.armor + armorResearch
        }
      }

      setDefense(defenseAvailable())
    }, [shieldsResearch, armorResearch, unit.base?.shields])

    return <div className={"unit"}>
      <div>
        <Unit type={unit} />
      </div>
      <div className={"upgrades"}>
        {unit.research.shields?
          <div>
            <Upgrade researchKind={"shields"} updateResearch={setShieldsResearch} value={shieldsResearch} />
