import React from 'react'
import {Unit} from "./Unit"
import {Upgrade} from "./Upgrade"
import { useSpring, animated } from 'react-spring'
import { immortal, marauder, marine } from '../SC2Data'

const DEFENDER_DEAD = "defender is dead"

const Bonus = ({attack, defender, style}) => {

  const d = new Damage(marauder, immortal, 0, [0, 0, 0])
  const results = []
console.log('hi', 1+1)
let i = -1
while(true){
  i++;
  const x = d.oneStep();
  if(x.length > 0){
    results.push(x)
  } else {
    console.log('victory in this many attacks', i)
    break;
  }
}

  return attack.bonuses.map((bonus, index) => {
    let bonusText = ` +${bonus.baseDamage} + (${bonus.researchBonus})`;

    return <>
      <div key={index}>
        Anti-{bonus.to}:
        {defender.types.includes(bonus.to) ?
        <span className={style}>
          {bonusText}
        </span>:
        <span style={{textDecoration: "wavy line-through red"}}>{ bonusText}</span>
        }
      </div>
    </>;
  })
}

const Bullet = ({animate, display}) => {
  if(display){
    if (animate){
      return <span className={"bullet"}>➡</span>
    } else {
      return <>➡</>
    }
  } else {
    return <></>
  }
}

function intersection(a, b) {
  var setA = new Set(a);
  var setB = new Set(b);
  var intersection = new Set([...setA].filter(x => setB.has(x)));
  return Array.from(intersection);
}

const canAttackTargetDefender = (attack, defender) => intersection(intersection(attack.targets, defender.types), ["Ground", "Air"]).length > 0

const Attacks = ({unit, defender, defaultIndex, setDefaultIndex, setDamage}) => {
  const toggleIndex = (index) => {
      if(index === defaultIndex){
        setDefaultIndex(null)
        setDamage(null)
      } else {
        setDefaultIndex(index)
      }
    }
  return unit.attacks.map((attack, index) => {
    const style = index === defaultIndex ? "main-weapon" : "alt-weapon";
    const available = canAttackTargetDefender(attack, defender)
    const cn = available ? "success" : "fail";

    return <div key={index} onClick={() => toggleIndex(index)} className={style}>
      <>
        <div className={"attack-name"}><Bullet animate={defaultIndex === null} display={available && (defaultIndex !== index)}/>{attack.name}</div>
        {index === defaultIndex ?
          <div>
            <>Base Damage: <span className={cn}>{attack.baseDamage} + ({attack.researchBonus})</span></>
            <Bonus attack={attack} defender={defender} style={cn}/>
          </div>
          : null
          }
      </>
    </div>
  })
}



export const Attacker = ({unit, defender, research, attackChoice, damage, setDamage}) => {
  const [attack, setAttack] = attackChoice;
  const [attackResearch, setAttackResearch] = research;

  let damageIndicator;
  if(unit.attacks.length > 0 ){
    if(damage){
      damageIndicator = <h1>⚔️ {damage}</h1>;
    } else {
      damageIndicator = <>⚔️ 👀 </>;
    }
  } else {
    damageIndicator = <></>;
  }

  return <div className={"unit"}>
    <div>
    <Unit type={unit}>
      {damageIndicator}
    </Unit>
      <Attacks unit={unit} defaultIndex={attack} setDefaultIndex={setAttack} defender={defender} setDamage={setDamage}/>
    </div>
    <div className={"upgrades"}>
      <Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} />
    </div>
  </div>
}




