class Damage{
  constructor( attacker, defender, attack, research, guardian=0) {
  const [attackResearch, armorResearch, shieldsResearch] = research
  let Spell = 1
  let Hallucinated = 1
  let Corrupted = 1
  let Hardened = 900
  let Prismatic = 1

  const bonusDamage = (att, def) => {
    return att.attacks[attack].bonuses.filter(bonus => def.types.includes(bonus.to)).reduce((total, amount) => {
      return total+amount.baseDamage+amount.researchBonus*attackResearch
    }, 0)
  }

  const damageDealt = (att, def) => {
    return (
      att.attacks[attack].baseDamage +
      attackResearch*att.attacks[attack].researchBonus +
      bonusDamage(att, def)
    )
  }

  const armorDefense = (unit) => {
    return unit.base.armor + armorResearch
  }

  const defenseAvailable = (unit) => {
    if(unit.base?.shields > 0){
      return shieldsResearch
    } else {
      return armorDefense(unit)
    }
  }

  const defenseApplied = (unit) => {
    return defenseAvailable(unit)*Spell*(2-Hallucinated)
  }

  const damageReceived = (att, def) => {
    return damageDealt(att, def)*Corrupted*Hallucinated*Prismatic - defenseApplied(def);
  }


  const damageCapped = (att, def) => {
    return Math.min(
      damageReceived(att, def),
      Hardened*Corrupted*Hallucinated+900*(1-Spell)
    );
  }

  const damageInflicted = (att, def) => {
    return Math.max(
      .5,
      damageCapped(att, def) - guardian
    );
  }

  this.totalDamage = (att, def) => {
    if(def.base.shields && damageInflicted(att, def) > def.base.shields + armorDefense(def)){
      console.log('def', def)
      return damageInflicted(att, def)-armorDefense(def)
    } else {
      return damageInflicted(att, def)
    }
  }


  this.att = JSON.parse(JSON.stringify(attacker));
  this.def = JSON.parse(JSON.stringify(defender));
  this.attackIndex = attack
}

oneStep = () => {
  const [att, def, aidx] = [this.att, this.def, this.attackIndex]
  const results = []
  let log;

  if(att.attacks[aidx] === undefined){
    return "👀"
  }
  if(this.def.base.health > 0){
    let totalDamage=0;

    let damage = this.totalDamage(att, def);
    for(var i = 0; i < att.attacks[aidx].repeats; i++){
      if(def.base?.shields > 0){
        if(damage <= def.base.shields){
          def.base.shields -= damage
          log = `${damage} to shields. (${def.base.shields} remaining)`
        } else {
          damage -= def.base.shields;
          def.base.shields = 0;
          def.base.health -= damage;
          log = `${damage} has overloaded shields. (${def.base.health} health remaining)`
        }
      } else {
        def.base.health -= damage;
        log = `${damage} damage. (${def.base.health} HP)`
      }
      totalDamage += damage;
      if(def.base.health < 0){
        const overkill = (100*-def.base.health/totalDamage).toFixed(1)
        log += ` (${overkill}% attack wasted)`
      }
      results.push({health: def.base.health, totalDamage: totalDamage})
    }
  }
  return results
}
}
