const DPS = ({attacker, defender, aidx,  attackResearch, shieldsResearch, armorResearch, setDefender}) => {
  console.log("damage", attacker, defender, aidx, [ attackResearch, shieldsResearch, armorResearch])
  let d = new Damage(attacker, defender, aidx, [ attackResearch, shieldsResearch, armorResearch])
  const backup = new Damage(JSON.parse(JSON.stringify(attacker)),
    JSON.parse(JSON.stringify(defender)), aidx,
    [ attackResearch, shieldsResearch, armorResearch]
    )
  const active = canAttackTargetDefender(attacker?.attacks[aidx], defender)
  const cns = ["button"];

  let text
  if(active){
    cns.push("active")
    if(d.oneShot()){
      text = <> Attack: ‚öîÔ∏è {d.oneShot().totalDamage}</>
    } else {
      text = <>Victory (reset)</>
    }
  } else {
    if (attacker && defender) {
      cns.push("inactive")
      if(attacker.attacks.map(attack => {
              return canAttackTargetDefender(attack, defender)
            }).filter(item => item).length > 0){
              text = <>Select a valid attack</>
            }
      else{
        cns.push("invalid")
        text = <>{attacker.name} can't attack {defender.name}</>
      }

    } else {
      cns.push("inactive")
      text =  <>Awaiting...</>
    }
  }

  const resetDefender = () => {
  //   const units = useUnits();
  //   if(defender?.name) {
  //    setDefender(units.find(unit => unit.name === defender.name))
  //    }
  }

  const updateHealth = () => {
    let newValues = d.oneShot();
    console.log('newValues', newValues)
    let newDefender = defender;
    console.log('aidx', aidx)
    if(newValues && aidx !== undefined && aidx !== null){
      console.log('staritng for loop')
      // for(var i = 0; i < attacker.attacks[aidx].repeats; i++){
        // console.log('looping', i)
        if(newValues.health > 0 || true){
        console.log("updatinghealth")
          let newValues = d.oneShot();
          if(newValues){
            newDefender = {
              ...newDefender,
              base: {
                ...newDefender.base,
                health: newValues.health,
                shields: newValues.shields,
              }
            }
            }
            console.log('newDefender', newDefender)
          d = new Damage(JSON.parse(JSON.stringify(attacker)),
          JSON.parse(JSON.stringify(newDefender)), aidx,
          [ attackResearch, shieldsResearch, armorResearch]
            )
            } else {
              console.log(">>>>>failed health chi")
            }
    //  }
     setDefender(newDefender)
    } else {
      resetDefender()
    }
  }

  return <div className={"DPS"}>
            <div><button className={cns.join(" ")} onClick={() => updateHealth()}>{text}</button></div>
            {active ? <>
              <div> <button onClick={() => resetDefender()}>Reset</button> </div>
              <div>{backup.eliminate()} attacks to eliminate</div>
          </> : null}
        </div>
}

const RandomButton = ({ position }) => {
  const units = useUnits();
  let button;

  if(units){
    const randomUnit = units[Math.floor(Math.random() * units.length)] 
    button = <button onClick={() => position(randomUnit)}>random : {randomUnit.name}</button>
  } else {
    button = <button>Loading Units...</button>
  }

  return <div className={"random-button"}>
      {button}
    </div>
}

const Middle = ({attacker, defender, aidx, research, setDefender, setAttacker}) => {
  const units = useUnits();
  if(!research || !units){
    return null;
  } else {
    const [attackResearch, setAttackResearch, shieldsResearch, setShieldsResearch, armorResearch, setArmorResearch] = research

    const baseShields = defender ?
      units.find(unit => unit.name === defender?.name)?.base?.shields:
      null ;

    console.log('units from Middle', units)
    const baseHealth = units.find(unit => unit.name === defender?.name)?.base.health

    const unitStats = defender ? 
    <div className={"unit-stats"}>

        <div><Upgrade researchKind={"attack"} updateResearch={setAttackResearch} value={attackResearch} /></div>
        {baseShields ?  
        <>
          <div>{defender?.research.shields?
          <Upgrade researchKind={"shields"} updateResearch={setShieldsResearch} value={shieldsResearch} />
          : null}</div>
        </>: null }


          <div><Upgrade researchKind={"armor"} updateResearch={setArmorResearch} value={armorResearch} /></div>
      </div> :
    null;

    let vitality = <>
         {baseShields ? <>
          <div>üßä</div>
          <div>{defender.base.shields} / {baseShields}</div>
          </>: null}
          <div>{defender? defender.base.armor + armorResearch*defender.research.armor: null} üõ°</div>
          <div>{defender? <>= {defender.base.armor} + {armorResearch}</>: null}</div>

          <div>‚ô•Ô∏è</div>
          <div>{defender? <>{defender.base.health} / {baseHealth}</> : null}</div>
    </>

    const randomButtons = <>
     {attacker ? null: <RandomButton position={setAttacker} />}
      {defender ? null: <RandomButton position={setDefender} />}
      </> 


    return <div style={{display:"flex", flexDirection: "column"}}>
      <div>
      {vitality}

      <DPS attacker={attacker} defender={defender} aidx={aidx}
          attackResearch={attackResearch} armorResearch={armorResearch} shieldsResearch={shieldsResearch}
          setDefender={setDefender}/>
      </div>


      <div>
        <h2>Research</h2>
        {unitStats}
        </div>
      <div>{randomButtons}</div>


      </div>

  }

  // console.log('attacker', attacker)
  // console.log('defender', defender)
  // console.log('research', research)

  // return(

  //   );
}
